CREATE SEQUENCE hibernate_sequence INCREMENT 1;

CREATE TABLE public.movie(
	id BIGSERIAL PRIMARY KEY NOT NULL,	
	title VARCHAR,
	producer VARCHAR,
	year int4,
	protagonists VARCHAR,
	synopsis VARCHAR,
	thumbnail VARCHAR
);

CREATE TABLE public.rating(
	id BIGSERIAL PRIMARY KEY NOT NULL,
	score int4,
	author VARCHAR,
	comment VARCHAR,
	movie_id BIGINT NOT NULL REFERENCES movie(id)	
);


